window.onload = function(){ animated_logo();};
 
  function animated_logo(){
    var body_div = document.getElementsByClassName("sdse")[0];
    
      var canvas = document.getElementById("animated");
      var context = canvas.getContext('2d');
      pixel_ratio = window.devicePixelRatio;
      var canvas_width = body_div.offsetWidth*0.9;
      var canvas_height = body_div.offsetWidth*0.9/3;
      canvas.width = canvas_width * pixel_ratio;
      canvas.height = canvas_height * pixel_ratio;
      canvas.style.width  = canvas_width + 'px';
      canvas.style.height = canvas_height + 'px';
      context.fillStyle = "#E1E1DF";
      context.fillRect(0, 0, canvas.width/3, canvas.height);
      stars(canvas, context, 1);
      setTimeout(function(){ title_text(canvas, context); }, 50);
      nzsa(canvas, context);
  }
  
  function stars(canvas, context, num){
      var star_colours = ["#425967","#425967","#425967","#425967"];
      if(num == 1)
      {
        var rand = Math.floor(4 * Math.random());
        star_colours[rand] = "#ffffff";
        context.clearRect(0, 0, canvas.width/3, canvas.height);
        context.fillStyle = "#E1E1DF";
        context.fillRect(0, 0, canvas.width/3, canvas.height);
      }
      context.fillStyle = star_colours[0];
      context.beginPath();
      context.arc(0.244*canvas.height, 0.425*canvas.height, 0.085*canvas.height, 0, 2 * Math.PI);
      context.fill();
      context.closePath();
      context.fillStyle = star_colours[1];
      context.beginPath();
      context.arc(0.499*canvas.height, 0.168*canvas.height, 0.086*canvas.height, 0, 2 * Math.PI);
      context.fill();
      context.closePath();
      context.fillStyle = star_colours[2];
      context.beginPath();
      context.arc(0.72*canvas.height, 0.358*canvas.height, 0.075*canvas.height, 0, 2 * Math.PI);
      context.fill();
      context.closePath();
      context.fillStyle = star_colours[3];
      context.beginPath();
      context.arc(0.499*canvas.height, 0.833*canvas.height, 0.096*canvas.height, 0, 2 * Math.PI);
      context.fill();
      context.closePath();
      if(num == 1)
      {
        setTimeout(function(){stars(canvas, context, 1)}, 500 + Math.random()*1500);
      }
  }
  
  function title_text(canvas, context){
     context.fillStyle = "#425967";
     context.textBaseline = "bottom";
     context.font = canvas.height*0.22 + "px Hepta Slab";  
     context.fillText("STATISTICS &", 4*canvas.width/11, canvas.height/2);
     context.fillStyle = "#5F6269";
     context.fillText("DATA SCIENCE", 4*canvas.width/11, 3 * canvas.height/4);
     context.fillStyle = "#1B1A15";
     context.fillText("EDUCATOR", 4*canvas.width/11, canvas.height);
  }
  
  function nzsa(canvas, context){
    var img = new Image()
      img.onload = function() {
        context.drawImage(img, 3*canvas.width/4, 0, canvas.width/4, (82/300)*canvas.width/4 );
      };
      img.src = 'images/nzsa.png';
  }



Statistics and Data Science Educator is an online journal set up for teachers by members of the New Zealand Statistical Association (NZSA) Education Committee under the auspices of NZSA. It has peer-reviewed lesson plans for teachers at all levels, early childhood to graduate.

The journal aims to improve the quality of teaching resources and the designing of tasks. Task design has a critical influence on students’ reasoning processes, what they learn, and on how they experience and understand the nature of statistical activity and thinking.


